// Javascript for list of food items
import {
    ArrowBackIosOutlined,
    ArrowForwardIosOutlined,
} from "@material-ui/icons";
import { useEffect } from "react";
import { useRef, useState } from "react";
import ListItem from "../listItem/ListItem";
import "./list.scss";

this.state = {
    disabled: false,
};

export default function List() {
    const [slideNumber, setSlideNumber] = useState(0);
    const { height, width } = useWindowDimensions();
    const MAX_NUM_SCROLLS = 6; // 10 items, 4 on screen at a time

    const listRef = useRef();

    const { disabled } = this.state;

    const handleClick = (direction) => {
        this.setState({
            disabled: true,
          });
        let distance = listRef.current.getBoundingClientRect().x - 50;
        if (direction === "left" && slideNumber > 0) {
            setSlideNumber(slideNumber - 1);
            listRef.current.style.transform = `translateX(${5 + ((width - 100)/4 - 5) + distance}px)`; // 5 + width / 5 is box size + margin
            setTimeout(() => {
                this.setState(() => ({
                  disabled: false,
                }));
              }, 5000);
        }
        if (direction === "right" && slideNumber < MAX_NUM_SCROLLS) {
            setSlideNumber(slideNumber + 1);
            listRef.current.style.transform = `translateX(${-5 - ((width - 100)/4 - 5) + distance}px)`;
            setTimeout(() => {
                this.setState(() => ({
                  disabled: false,
                }));
              }, 5000);
        }

    };
    return (
        <div className="list">
            <span className="listTitle">Recommended for You</span>
            <div className="wrapper">
                <ArrowBackIosOutlined
                    className="sliderArrow left"
                    onClick={() => handleClick("left")}
                    disabled={disabled}
                />
                <div className="listItems" ref={listRef}>
                    <ListItem index={0} />
                    <ListItem index={1} />
                    <ListItem index={2} />
                    <ListItem index={3} />
                    <ListItem index={4} />
                    <ListItem index={5} />
                    <ListItem index={6} />
                    <ListItem index={7} />
                    <ListItem index={8} />
                    <ListItem index={9} />
                </div>
                <ArrowForwardIosOutlined
                    className="sliderArrow right"
                    onClick={() => handleClick("right")}
                    disabled={disabled}
                />
            </div>
        </div>
    );
};

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}

export function useWindowDimensions() {
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

    useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
        }

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return windowDimensions;
}
