// ListItem.jsx - list item on a movie list
import { Add, PlayArrow, ThumbDownOutlined, ThumbUpAltOutlined } from "@material-ui/icons";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./listItem.scss";

export default function ListItem({index, item}) { // item is _id
    const [isHovered, setIsHovered] = useState(false); // whether hovering over list item
    const [movie, setMovie] = useState(null);

    useEffect(()=>{
        const getMovie = async () => {
            try {
                const res = await axios.get("/movies/find/" + item, 
                    {
                        headers: {
                            token: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzMzg5MWI4MDJiYjkwNTBkM2Q3NWE0YyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY2NDc0ODc3NiwiZXhwIjoxNjY1MTgwNzc2fQ.gIOudPuLYHVhReeL_prUG1mmt9z26EBH89HJa_WskJs"
                        }
                    }
                );
                setMovie(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        getMovie();
    }, [item]); // is run whenever item is changed

    return (
        <Link to={{pathname: "/watch", movie: movie}}> {/* go to movie watch page when user clicks on the list item */}
            <div 
                className="listItem" 
                style={{left: isHovered && index * 225 - 50 + index * 2.5}} // TODO: fix alignment math
                onMouseEnter={()=>setIsHovered(true)} 
                onMouseLeave={()=>setIsHovered(false)}
            >
                <img 
                    // src="https://media-exp1.licdn.com/dms/image/C5603AQEkRAgsiHax8A/profile-displayphoto-shrink_800_800/0/1649278831747?e=1669852800&v=beta&t=6AJ54Hf4pEmLsAfJkPF3G0mqauZIopyGDXRoUYfPKNY",
                    src = {movie.img}
                    alt="" 
                />
                {isHovered && ( // play video when hovering over list item
                    <>
                        <video src={movie.trailer} autoPlay={true} muted loop /> {/* need muted field when autoplaying on chrome */}
                        <div className="itemInfo">
                            <div className="icons">
                                <PlayArrow className="icon"/>
                                <Add className="icon"/>
                                <ThumbUpAltOutlined className="icon"/>
                                <ThumbDownOutlined className="icon"/>
                            </div>
                            <div className="itemInfoTop">
                                <span>{movie.duration}</span>
                                <span className="limit">{movie.limit}+</span>
                                <span>{movie.year}</span>
                            </div>
                            <div className="desc">{movie.desc}</div>
                            <div className="genre">{movie.genre}</div>
                        </div>
                    </>
                )}
            </div>
        </Link>
    );
}
