/* Home.jsx - main javascript file for home page */
import { useEffect } from "react";
import { useState } from "react";
import Featured from "../../components/featured/Featured";
import List from "../../components/list/List";
import Navbar from "../../components/navbar/Navbar";
import "./home.scss";
import axios from "axios";

const Home = ({type}) => {
    const [lists, setLists] = useState([]);
    const [genre, setGenre] = useState(null);

    useEffect(() => {
        const getRandomLists = async () => {
            try { // set API path {"proxy": "http:localhost:8000/api"} from package.json
                const res = await axios.get(
                    `lists${type ? "?type=" + type : ""}${genre ? "&genre=" + genre : ""}`, 
                    {
                        headers: {
                          token: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzMzg5MWI4MDJiYjkwNTBkM2Q3NWE0YyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY2NDc0ODc3NiwiZXhwIjoxNjY1MTgwNzc2fQ.gIOudPuLYHVhReeL_prUG1mmt9z26EBH89HJa_WskJs"
                        //   "Bearer "+JSON.parse(localStorage.getItem("user")).accessToken,
                        },
                    }
                ); // get response from API
                // console.log(res);
                setLists(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        getRandomLists();
    }, [type, genre]); // dependencies: when  type or genre is changed useEffect is called
    return (
        <div className='home'>
            <Navbar /> {/* navigation at top */}
            <Featured type={type}/> {/* featured panel */}
            {lists.map(list => (
                <List /> 
            ))}

        </div>
    )  
}

export default Home
